				blockEncounterOnSameDateEncounter(jq("#encounterDate").find("input[type=hidden]"),"block");

                var visitDate = '';
                var patientUUID = '';
                var currentProgram = null;
                var previousPrograms = null;
                var serverResponse = [];

                jq.ajax({
                    type: "GET",
                    url: '/' + OPENMRS_CONTEXT_PATH + "/ws/rest/v1/programenrollment?v=full&patient=" + patientUUID,
                    dataType: "json",
                    async: false,
                    success: function (data) {
                        serverResponse = data.results;
                    }
                });

                for (var i = 0; i < serverResponse.length; i++) {
                    var obj = serverResponse[i];
                    if (obj.display === "TB Program" && obj.dateCompleted === "") {
                        currentProgram = obj;
                    } else if (obj.display === "TB Program" && obj.dateCompleted !== "" && ((new Date(visitDate)) 
                        <= (new Date(obj.dateCompleted))) && ((new Date(visitDate)) >= (new Date(obj.dateEnrolled)))) {
                        previousPrograms = obj;
                    }
                }

                //Block encounter if there is no active program which ended before visit date//
                /*
                    * queryParameters -> handles the query string parameters
                    * queryString -> the query string without the fist '?' character
                    * re -> the regular expression
                    * m -> holds the string matching the regular expression
                */
                var queryParameters = {}, queryString = location.search.substring(1),
                    re = /([^&=]+)=([^&]*)/g, m;
                    // Creates a map with the query string parameters
                    while (m = re.exec(queryString)) {
                        queryParameters[decodeURIComponent(m[1])] = decodeURIComponent(m[2]);
                    } if (currentProgram === null && previousPrograms === null) {
                        alert('This Patient Has No Active TB Summary Page. You will be redirected to the patient Dashboard');
                        location.href = window.location.protocol + "//" + window.location.host +
                            '/' + OPENMRS_CONTEXT_PATH + '/coreapps/clinicianfacing/patient.page?patientId='+ queryParameters['patientId'];
                    }

				//change results for a selected test
